// LOCAL UTILITIES
function where(array1, array2, comparer) {
    return array1.map((a) => {
        return array2.find((b) => comparer(a, b));
    });
}
function uniq(array, comparer) {
    const uniqArr = [];
    for (let i = 0; i < array.length; i++) {
        const item = array[i];
        let exists = false;
        for (let j = 0; j < uniqArr.length; j++) {
            if (comparer(uniqArr[j], item)) {
                exists = true;
            }
        }
        if (!exists) {
            uniqArr.push(item);
        }
    }
    return uniqArr;
}
function noWhitespace(str) {
    return str.replace(" ", "");
}
function flat(arr) {
    let newArr = [];
    for (let i = 0; i < arr.length; i++) {
        const item = arr[i];
        if (Array.isArray(item)) {
            newArr = newArr.concat(flat(item));
            continue;
        }
        newArr.push(item);
    }
    return newArr;
}
